import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
  ImgPanier,
  ProduitContainer,
  LeftSide,
  RightSide,
  Nomp,
} from "./PanierElements.js";

const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState([]);
  const [Products, setProducts] = useState([]);

  useEffect(() => {
    const email = localStorage.getItem("email");
    const getPanier = async () => {
      const reponse = await axios
        .get(
          `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
        )
        .then((result) => {
          return result.data.items;
        })
        .catch((err) => {
          console.log(err);
        });
      setCurrentPanier(reponse);
      console.log(reponse);
    };
    getPanier();
  }, []);
  useEffect(() => {
    setProducts([]);
    CurrentPanier.map((id) => {
      console.log(id.product_id);
      const idtoProduct = async () => {
        const reponse = await axios
          .get(
            `https://whispering-bastion-00988.herokuapp.com/api/produits/${id.product_id}`
          )
          .then((result) => {
            console.log(result.data);
            return result.data;
          })
          .catch((err) => {
            console.log(err.message);
          });
        console.log(reponse);
        if (!Products.includes(reponse)) {
          reponse.qtycmd = id.qty;
          setProducts((prevProducts) => [...prevProducts, reponse]);
        }
      };
      idtoProduct();
    });
  }, [CurrentPanier]);
  return (
    <PanierWrapper>
      <PanierContainer>
        <LeftSide>
          {Products.map((p, key) => {
            return (
              <ProduitItem>
                <ProduitContainer>
                  <ImgPanier src={p.imageurl} />
                  <Nomp>{p.nom}</Nomp>
                  <p>Quantit√© : {p.qtycmd}</p>
                </ProduitContainer>
              </ProduitItem>
            );
          })}
        </LeftSide>
        <RightSide></RightSide>
      </PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
