import React, { useEffect, useState, useRef } from "react";
import "./Catalogue_style.css";
import axios from "axios";
import { BiSearch } from "react-icons/bi";
import {
  StyledFormContainer,
  StyledFormWrapper,
  CardImg,
  Form,
  Input,
  ButtonR,
} from "./CatalogueElements.js";
export const Catalogue = () => {
  const [Products, setProducts] = useState([]);
  const [UpdatedProducts, setUpdatedProducts] = useState([]);
  const [input, setInput] = useState("");
  const [barOpened, setBarOpened] = useState(false);
  const formRef = useRef();
  const inputFocus = useRef();

  useEffect(() => {
    axios
      .get("https://productsapi1.herokuapp.com/api/produits")
      .then((Response) => {
        setProducts(Response.data);
        if (input === "") {
          setUpdatedProducts(Response.data);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, [Products]);
  useEffect(() => {
    if (value === "") {
      setUpdatedProducts(Products);
    } else updateProducts(value);
  }, [input]);
  const HandleSearch = (value) => {
    setInput(value);
    let valeur = valeur;
  };
  const updateProducts = (value) => {
    setUpdatedProducts([]);
    Products.map((product) => {
      if (product.nom.includes(value)) {
        setUpdatedProducts((prev) => [...prev, product]);
      }
    });
  };
  const onFormSubmit = (e) => {
    // When form submited, clear input, close the searchbar and do something with input
    e.preventDefault();
    setInput("");
    setBarOpened(true);
    // After form submit, do what you want with the input value
    console.log(`Form was submited with input: ${input}`);
  };

  return (
    <div>
      <StyledFormWrapper>
        <Form
          barOpened={barOpened}
          onClick={() => {
            // When form clicked, set state of baropened to true and focus the input
            setBarOpened(true);
            inputFocus.current.focus();
          }}
          // on focus open search bar
          onFocus={() => {
            setBarOpened(true);
            inputFocus.current.focus();
          }}
          // on blur close search bar
          onBlur={() => {
            setBarOpened(false);
          }}
          // On submit, call the onFormSubmit function
          onSubmit={onFormSubmit}
          ref={formRef}
        >
          <ButtonR type="submit" barOpened={barOpened}>
            <BiSearch />
          </ButtonR>
          <Input
            onChange={(e) => {
              HandleSearch(value);
            }}
            ref={inputFocus}
            value={input}
            barOpened={barOpened}
            placeholder="Rechercher un produit..."
          />
        </Form>
        <StyledFormContainer>
          {UpdatedProducts.map((product) => {
            return (
              <div class="el-wrapper">
                <div class="box-up">
                  <img className="img" src={product.imageurl} alt="" />
                  <div class="img-info">
                    <div class="info-inner">
                      <span class="p-name">{product.nom}</span>
                      <span class="p-company">{product.type}</span>
                    </div>
                    <div class="a-size">
                      <span class="size">
                        Desciption :{product.description}
                      </span>
                    </div>
                  </div>
                </div>

                <div class="box-down">
                  <div class="h-bg">
                    <div class="h-bg-inner"></div>
                  </div>

                  <a class="cart" href="#">
                    <span class="price">{product.prix}$</span>
                    <span class="add-to-cart">
                      <span class="txt">Ajouter au panier</span>
                    </span>
                  </a>
                </div>
              </div>
            );
          })}
        </StyledFormContainer>
      </StyledFormWrapper>
    </div>
  );
};

export default Catalogue;
