import axios from "axios";
import React, { useState, useEffect } from "react";
import Select from "react-select";
import {
  StyledFormWrapper,
  StyledForm,
  StyledInput,
  StyledButton,
  Styledh1,
  StyledFormContainer,
  StyledError,
  Notificationvert,
  Notificationrouge,
  StyledArea,
  ImgProduct,
} from "./OrderElements.js";
const initalState = {
  name: "",
  email: "",
  postCode: "",
  district: "",
  country: "",
};

const options = [
  { value: "1-Adrar", label: "1-Adrar" },
  { value: "2-Chlef", label: "Chlef" },
  { value: "3-Laghout", label: "Laghout" },
  { value: "4-Oum El bouaghi", label: "Oum El bouaghi" },
  { value: "5-Batna", label: "Batna" },
  { value: "6-Béjaïa", label: "Béjaïa" },
  { value: "7-Biskra", label: "Biskra" },
  { value: "8-Béchar", label: "Béchar" },
  { value: "9-Blida", label: "Blida" },
  { value: "10-Bouira", label: "Bouira" },
  { value: "11-Tamanrasset", label: "Tamanrasset" },
  { value: "12-Tébessa", label: "Tébessa" },
  { value: "13-Tlemcen", label: "Tlemcen" },
  { value: "14-Tiaret", label: "Laghout" },
  { value: "15-Tizi Ouzou", label: "Laghout" },
  { value: "16-Alger", label: "Laghout" },
  { value: "17-Djelfa", label: "Laghout" },
  { value: "18-Jijel", label: "Laghout" },
  { value: "19-Sétif", label: "Laghout" },
  { value: "20-Saïda", label: "Laghout" },
  { value: "21-Skikda", label: "Laghout" },
  { value: "22-Sidi Bel Abbès", label: "Laghout" },
  { value: "23-Annaba", label: "Laghout" },
  { value: "24-Guelma", label: "Laghout" },
  { value: "25-Constantine", label: "Laghout" },
  { value: "26-Médéa", label: "Laghout" },
  { value: "27-Mostaganem", label: "Laghout" },
  { value: "28-M'Sila", label: "Laghout" },
  { value: "29-Mascara", label: "Laghout" },
  { value: "30-Ouargla", label: "Laghout" },
  { value: "31-Oran", label: "Laghout" },
  { value: "32-El Bayadh", label: "Laghout" },
  { value: "33-Illizi", label: "Laghout" },
  { value: "34-Bordj Bou Arreridj", label: "Laghout" },
  { value: "35-Boumerdès", label: "Laghout" },
  { value: "36-Tarf", label: "Laghout" },
  { value: "37-Tindouf", label: "Laghout" },
  { value: "38-Tissemsilt", label: "Laghout" },
  { value: "39-El Oued", label: "Laghout" },
  { value: "40-Khenchela", label: "Laghout" },
  { value: "41-Souk Ahras", label: "Laghout" },
  { value: "42-Tipaza", label: "Laghout" },
  { value: "43-Mila", label: "Laghout" },
  { value: "44-Aïn Defla", label: "Laghout" },
  { value: "Laghout", label: "Laghout" },
  { value: "Laghout", label: "Laghout" },
  { value: "Laghout", label: "Laghout" },
  { value: "Laghout", label: "Laghout" },
  { value: "Laghout", label: "Laghout" },
  { value: "Laghout", label: "Laghout" },
  { value: "Laghout", label: "Laghout" },
];
const Order = (props) => {
  const [state, setState] = useState(initalState);
  const [selectedOption, setSelectedOption] = useState(null);
  useEffect(() => {}, []);
  const handleInput = (e) => {
    const inputName = e.currentTarget.name;
    const value = e.currentTarget.value;
    setState((prev) => ({ ...prev, [inputName]: value }));
  };
  const handleSubmit = (e) => {
    const CurrentPanier = props.location.items;
    const nom = localStorage.getItem("nom");
    const email = localStorage.getItem("email");
    e.preventDefault();
    if (state.quantite < 1) {
      return;
    }
    axios
      .post(`https://managecartandorders.herokuapp.com/api/order/create`, {
        user: { name: nom, email: email },
        billingAddress: {
          name: nom,
          email: email,
          postCode: state.postCode,
          district: state.district,
          country: state.country,
        },
        items: {},
      })
      .then((response) => {})
      .catch((error) => {});
  };
  return (
    <div>
      <StyledFormWrapper>
        <StyledFormContainer>
          <StyledForm onSubmit={handleSubmit}>
            <Styledh1>Entrez vos cordonnés</Styledh1>
            <br />

            <Select
              className="select"
              defaultValue={selectedOption}
              onChange={setSelectedOption}
              options={options}
              placeholder="Wilaya"
            />
            <label htmlFor="postCode"></label>
            <StyledInput
              type="text"
              name="postCode"
              value={state.postCode}
              onChange={handleInput}
              placeholder="Code postal..."
            />
            <label htmlFor="deliveryAddress"></label>
            <StyledInput
              type="text"
              name="deliveryAddress"
              value={state.nom}
              onChange={handleInput}
              placeholder="Adresse de livraison..."
            />
            <label htmlFor="phoneNumber"></label>
            <StyledInput
              type="text"
              name="phoneNumber"
              value={state.quantite}
              onChange={handleInput}
              placeholder="Numéro de téléphone..."
            />
            <StyledButton>Passer la commande</StyledButton>
          </StyledForm>
        </StyledFormContainer>
      </StyledFormWrapper>
    </div>
  );
};

export default Order;
