import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
} from "./PanierElements.js";

const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState([]);
  const [Products, setProducts] = useState([]);

  useEffect(() => {
    const email = localStorage.getItem("email");
    const getPanier = async () => {
      const reponse = await axios
        .get(
          `https://managecartandorders.herokuapp.com/api/cart/cart?email=za@gmail.com`
        )
        .then((result) => {
          return result.data.items;
        })
        .catch((err) => {
          console.log(err);
        });
      setCurrentPanier(reponse);
      console.log(reponse);
    };
    getPanier();
  }, []);
  useEffect(() => {
    setProducts([]);
    CurrentPanier.map((id) => {
      console.log(id.product_id);
      const idtoProduct = async () => {
        const reponse = await axios
          .get(
            `https://whispering-bastion-00988.herokuapp.com/api/produits/${id.product_id}`
          )
          .then((result) => {
            console.log(result.data);
            return result.data;
          })
          .catch((err) => {
            console.log(err.message);
          });
        console.log(reponse);
        if (Products.includes(reponse)) {
          setProducts((prevProducts) => [...prevProducts, reponse]);
        }
      };
      idtoProduct();
    });
  }, [CurrentPanier]);
  return (
    <PanierWrapper>
      <PanierContainer>
        {Products.map((p, key) => {
          return (
            <ProduitItem>
              <p>{p.nom}</p>
            </ProduitItem>
          );
        })}
      </PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
