import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
} from "./PanierElements.js";

const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState();
  useEffect(() => {
    const email = localStorage.getItem("email");
    const getPanier = async () => {
      await axios
        .get(
          `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
        )
        .then((reponse) => {
          setCurrentPanier(reponse.data.items);
          console.log(CurrentPanier);
        })
        .catch((err) => {
          console.log(err);
        });
    };
    axios
      .get(
        `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
      )
      .then((reponse) => {
        setCurrentPanier(reponse.data.items);
        console.log(CurrentPanier);
      })
      .catch((err) => {
        console.log(err);
      });
  });
  return (
    <PanierWrapper>
      <PanierContainer>
        {CurrentPanier.map((produit, key) => {
          <ProduitItem></ProduitItem>;
        })}
      </PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
