import axios from "axios";
import React, { useEffect, useRef, useState } from "react";
import {
  GStockContainer,
  Wrapper,
  TableTh,
  GStockTable,
  ProductImg,
  TableTd,
  TableLink,
  TableButton,
  BtnAjouterP,
  Form,
  ButtonR,
  Input,
} from "./GstockElements.js";
import { VscCheck, VscClose } from "react-icons/vsc";
import { MdDelete, MdModeEdit } from "react-icons/md";
import { BiSearch } from "react-icons/bi";
import { RiAddCircleLine } from "react-icons/ri";
const Gstock = () => {
  const [Products, setProducts] = useState([]);
  const [UpdatedProducts, setUpdatedProducts] = useState([]);
  const [Compteur, setCompteur] = useState(0);
  const [input, setInput] = useState("");
  const [barOpened, setBarOpened] = useState(true);
  const formRef = useRef();
  const inputFocus = useRef();
  const HandleSearch = (e) => {
    setInput(e.target.value);
    if (e.target.value !== "") {
      updateProducts(e.target.value);
    }
  };
  const updateProducts = (value) => {
    Products.map((product) => {
      if (product.nom.includes(value)) {
        setUpdatedProducts = product;
      }
    });
  };
  const onFormSubmit = (e) => {
    // When form submited, clear input, close the searchbar and do something with input
    e.preventDefault();
    setInput("");
    setBarOpened(true);
    // After form submit, do what you want with the input value
    console.log(`Form was submited with input: ${input}`);
  };
  useEffect(() => {
    axios
      .get("https://productsapi1.herokuapp.com/api/produits")
      .then((Response) => {
        setProducts(Response.data);
        console.log(Products);
      })
      .catch((error) => {});
  }, [Products]);
  const HandleEtat = (nombre) => {
    if (nombre > 0) {
      return <VscCheck color="green" />;
    } else return <VscClose color="red" />;
  };
  const DeleteHandler = (id) => {
    axios
      .delete(`https://productsapi1.herokuapp.com/api/produits/${id}`)
      .then((response) => {
        console.log(response.data);
      })
      .catch();
  };
  return (
    <div>
      <Wrapper>
        <GStockContainer>
          <BtnAjouterP to="/GestionProduit/AjouterProduit">
            <RiAddCircleLine size="48" />
          </BtnAjouterP>
          <h3>Ajouter un produit</h3>
          <Form
            barOpened={barOpened}
            onClick={() => {
              // When form clicked, set state of baropened to true and focus the input
              setBarOpened(true);
              inputFocus.current.focus();
            }}
            // on focus open search bar
            onFocus={() => {
              setBarOpened(true);
              inputFocus.current.focus();
            }}
            // on blur close search bar
            onBlur={() => {
              setBarOpened(true);
            }}
            // On submit, call the onFormSubmit function
            onSubmit={onFormSubmit}
            ref={formRef}
          >
            <ButtonR type="submit" barOpened={barOpened}>
              <BiSearch />
            </ButtonR>
            <Input
              onChange={HandleSearch}
              ref={inputFocus}
              value={input}
              barOpened={barOpened}
              placeholder="Rechercher un produit..."
            />
          </Form>
          <GStockTable>
            <tr>
              <TableTh>Num Produits</TableTh>
              <TableTh>Nom</TableTh>
              <TableTh>Image</TableTh>
              <TableTh>Prix</TableTh>
              <TableTh>Quantité</TableTh>
              <TableTh>État</TableTh>
              <TableTh>Modifier</TableTh>
              <TableTh>Supprimer</TableTh>
            </tr>
            {Products.map((product) => {
              return (
                <tr id={product.id}>
                  <TableTd>{Compteur}</TableTd>
                  <TableTd>{product.nom}</TableTd>
                  <TableTd>
                    <ProductImg src={product.imageurl} />
                  </TableTd>

                  <TableTd>{product.prix}</TableTd>

                  <TableTd>{product.quantite}</TableTd>
                  <TableTd>{HandleEtat(product.quantite)}</TableTd>
                  <TableTd>
                    <TableLink
                      to={{
                        pathname: `/AdminDashboard/GestionProduit/ModifierProduit/${product.id}`,
                        id: product.id,
                      }}
                    >
                      <MdModeEdit color="green" size="24" />
                    </TableLink>
                  </TableTd>
                  <TableTd>
                    <TableButton
                      onClick={() => {
                        if (
                          window.confirm(
                            "Voulez-vous vraiment supprimer ce produit?"
                          )
                        ) {
                          DeleteHandler(product.id);
                          setTimeout(() => {
                            alert("Produit supprimé avec succès !");
                          }, 1500);
                        }
                      }}
                    >
                      <MdDelete color="red" size="24" />
                    </TableButton>
                  </TableTd>
                </tr>
              );
            })}
          </GStockTable>
        </GStockContainer>
      </Wrapper>
    </div>
  );
};

export default Gstock;
