import React, { useState, useEffect } from "react";
//-------------------Components------------------
import Navbar from "./components/Navbar/Navbar.js";
//------------------Components_Fin------------------
//------------------GlobalStyles------------------
import GlobalStyle, { WelcomeHero } from "./GlobalStyles.js";
import "./styles.css";
//------------------GlobalStyles_Fin------------------
//------------------Pages------------------
import Acceuil from "./pages/Acceuil/Acceuil.js";
import SignUp from "./pages/SignUp/SignUp.js";
import LogIn from "./pages/LogIn/LogIn.js";
import Catalogue from "./pages/Catalogue/Catalogue.js";
import UserProfile from "./pages/Profiles/UserProfile/UserProfile.js";
import AdminProfile from "./pages/Profiles/AdminProfile/AdminProfile.js";
import SuperAdminProfile from "./pages/Profiles/SuperAdminProfile/SuperAdminProfile.js";
import VerifyRole from "./pages/Profiles/VerifyRole.js";

//------------------Pages_Fin------------------
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
function App() {
  const state = localStorage.getItem("state");
  const [authorized, setAuthorized] = useState(state ? true : false);
  const [Role, setRole] = useState("");

  return (
    <Router>
      <GlobalStyle />
      <WelcomeHero>
        <Navbar setAuthorized={setAuthorized} />
        <Switch>
          <Route path="/" exact component={Acceuil}></Route>
          <Route path="/SignUp" exact component={SignUp}></Route>
          <Route
            path="/LogIn"
            exact
            component={() => (
              <LogIn setAuthorized={setAuthorized} setRole={setRole} />
            )}
          ></Route>
          <Route
            path="/VerifyRole"
            exact
            component={() => <VerifyRole authorized={authorized} Role={Role} />}
          ></Route>
          <Route
            path="/UserDashboard"
            exact
            component={() => <UserProfile authorized={authorized} />}
          ></Route>
          <Route
            path="/AdminDashboard"
            exact
            component={() => <AdminProfile authorized={authorized} />}
          ></Route>
          <Route
            path="/SuperAdminDashboard"
            exact
            component={() => <SuperAdminProfile authorized={authorized} />}
          ></Route>
          <Route path="/Catalogue" exact component={Catalogue}></Route>
        </Switch>
      </WelcomeHero>
    </Router>
  );
}

export default App;
