import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
} from "./PanierElements.js";

const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState();
  useEffect(() => {
    const email = localStorage.getItem("email");
    const getPanier = async () => {
      const reponse = await axios
        .get(
          `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
        )
        .then((result) => {
          console.log(result);
          return result;
        })
        .catch((err) => {
          console.log(err);
        });
      return reponse;
    };
    getPanier();
    setCurrentPanier(getPanier());
    console.log(CurrentPanier);
  });
  return (
    <PanierWrapper>
      <PanierContainer>
        {CurrentPanier &&
          CurrentPanier.map((produit, key) => {
            <h1>produit.qty</h1>;
          })}
      </PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
