import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
} from "./PanierElements.js";
const getPanier = async (email) => {
  const reponse = await axios
    .get(
      `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
    )
    .then((result) => {
      console.log(result);
      return result;
    })
    .catch((err) => {
      console.log(err);
    });
  return reponse;
};
const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState();
  useEffect(() => {
    const email = localStorage.getItem("email");
    setCurrentPanier(getPanier(email));
  });
  return (
    <PanierWrapper>
      <PanierContainer></PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
