import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
} from "./PanierElements.js";

const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState([]);
  useEffect(() => {
    const email = localStorage.getItem("email");
    axios
      .get(
        `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
      )
      .then((result) => {
        setCurrentPanier(result.data.items);
      })
      .catch((err) => {
        console.log(err);
      });
  });
  return (
    <PanierWrapper>
      <PanierContainer>
        {Array.from(CurrentPanier).map((produit, key) => {
          return <ProduitItem>{produit}</ProduitItem>;
        })}
      </PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
