import axios from "axios";
import React, { useState, useEffect } from "react";
import {
  StyledFormWrapper,
  StyledForm,
  StyledInput,
  StyledButton,
  Styledh1,
  StyledFormContainer,
  StyledError,
  Notificationvert,
  Notificationrouge,
  StyledArea,
  ImgProduct,
} from "./OrderElements.js";
const initalState = {
  name: "",
  email: "",
  postCode: "",
  district: "",
  country: "",
};

const Order = (props) => {
  const [state, setState] = useState(initalState);
  useEffect(() => {}, []);
  const handleInput = (e) => {
    const inputName = e.currentTarget.name;
    const value = e.currentTarget.value;
    setState((prev) => ({ ...prev, [inputName]: value }));
  };
  const handleSubmit = (e) => {
    const CurrentPanier = props.location.items;
    const nom = localStorage.getItem("nom");
    const email = localStorage.getItem("email");
    e.preventDefault();
    if (state.quantite < 1) {
      return;
    }
    axios
      .post(`https://managecartandorders.herokuapp.com/api/order/create`, {
        user: { name: nom, email: email },
        billingAddress: {
          name: nom,
          email: email,
          postCode: state.postCode,
          district: state.district,
          country: state.country,
        },
        items: {},
      })
      .then((response) => {})
      .catch((error) => {});
  };
  return (
    <div>
      <StyledFormWrapper>
        <StyledFormContainer>
          <StyledForm onSubmit={handleSubmit}>
            <Styledh1>Entrez vos cordonnés</Styledh1>
            <br />

            <label htmlFor="Wilaya"></label>
            <StyledInput
              type="text"
              name="Wilaya"
              value={state.Wilaya}
              onChange={handleInput}
              placeholder="Wilaya"
            />
            <label htmlFor="postCode"></label>
            <StyledInput
              type="text"
              name="postCode"
              value={state.postCode}
              onChange={handleInput}
              placeholder="Code postal..."
            />
            <label htmlFor="phoneNumber"></label>
            <StyledInput
              type="text"
              name="phoneNumber"
              value={state.quantite}
              onChange={handleInput}
              placeholder="Numéro de téléphone..."
            />
            <label htmlFor="deliveryAddress"></label>
            <StyledInput
              type="text"
              name="deliveryAddress"
              value={state.nom}
              onChange={handleInput}
              placeholder="Adresse de livraison..."
            />

            <label htmlFor="imageurl"></label>
            <StyledButton>Passer la commande</StyledButton>
          </StyledForm>
        </StyledFormContainer>
      </StyledFormWrapper>
    </div>
  );
};

export default Order;
