import axios from "axios";
import React, { useEffect, useState } from "react";
import {
  ProduitWrapper,
  ProduitContainer,
  ImgProduit,
  InfoProduitContainer,
} from "./FicheProduitElements.js";
import "./FicheProduit_style.css";
function FicheProduit(props) {
  const [CurrentProduct, setCurrentProduct] = useState({});
  const [Qty, setQty] = useState(1);
  useEffect(() => {
    axios
      .get(
        `https://whispering-bastion-00988.herokuapp.com/api/produits/${props.location.pathname.substring(
          11
        )}`
      )
      .then((reponse) => {
        setCurrentProduct(reponse.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  const incrementeQty = () => {
    setQty(Qty + 1);
  };
  return (
    <ProduitWrapper>
      <ProduitContainer>
        <ImgProduit src={CurrentProduct.imageurl} />
        <InfoProduitContainer>
          <h1>{CurrentProduct.nom}</h1>
          <br />
          <h3>{CurrentProduct.prix}$</h3>
          <br />
          <p>{CurrentProduct.description}</p>
          <br />
          <h4>Quantit√© en stock : {CurrentProduct.quantite}</h4>
          <br />
          <div class="quantity buttons_added">
            <input type="button" value="-" class="minus" />
            <input
              type="number"
              step="1"
              min="1"
              max={CurrentProduct.quantite}
              name="quantity"
              value={Qty}
              title="Qty"
              class="input-text qty text"
              size="4"
              pattern=""
              inputmode=""
            />
            <input
              type="button"
              value="+"
              class="plus"
              onClick={incrementeQty}
            />
          </div>
          <button className="btn_addcart">Ajouter au panier</button>
        </InfoProduitContainer>
      </ProduitContainer>
    </ProduitWrapper>
  );
}

export default FicheProduit;
