import React, { useState } from "react";
import {
  StyledFormWrapper,
  StyledForm,
  StyledInput,
  StyledButton,
  StyledError,
  Notificationvert,
  Notificationrouge,
  Styledh1,
  StyledFormBlue,
  StyledKagu,
  Styledp,
  Styleda,
  StyledAddiction,
  StyledFormContainer,
  StyledMeuble,
} from "../../GlobalStyles.js";
const initalState = {
  nom: "",
  prenom: "",
  email: "",
  numtel: "",
  password: "",
  roles: "user",
};
const Gstock = () => {
  const [state, setState] = useState(initalState);
  const handleInput = (e) => {
    const inputName = e.currentTarget.name;
    const value = e.currentTarget.value;
    if (inputName === "confirm_password") {
      console.log(value);
      setConfirm(value);
    }
    setState((prev) => ({ ...prev, [inputName]: value }));
  };
  return (
    <div>
      <StyledFormWrapper>
        <StyledFormContainer>
          <StyledForm onSubmit={handleSubmit}>
            <center>
              <StyledKagu src={KaguBlue} />
            </center>
            <Styledh1>Inscrivez-Vous !</Styledh1>
            <br />
            <label htmlFor="nom"></label>
            <StyledInput
              type="text"
              name="nom"
              value={state.nom}
              onChange={handleInput}
              placeholder="Nom..."
            />
            <label htmlFor="prenom"></label>
            <StyledInput
              type="text"
              name="prenom"
              value={state.prenom}
              onChange={handleInput}
              placeholder="Prénom..."
            />
            <label htmlFor="email"></label>
            <StyledInput
              type="email"
              name="email"
              value={state.email}
              onChange={handleInput}
              placeholder="Email..."
            />
            <label htmlFor="tel"></label>
            <StyledInput
              type="tel"
              name="numtel"
              value={state.numtel}
              onChange={handleInput}
              placeholder="Numéro de téléphone..."
            />
            <label htmlFor="password"></label>
            <StyledInput
              type="password"
              name="password"
              value={state.password}
              onChange={handleInput}
              placeholder="Mot de passe..."
            />
            <label htmlFor="confirm_password"></label>
            <StyledInput
              type="password"
              name="confirm_password"
              value={confirm}
              onChange={handleInput}
              placeholder="Confirmez le mot de passe..."
            />
            {error && (
              <StyledError>
                <p>{error}</p>
              </StyledError>
            )}
            {Notir && (
              <Notificationrouge>
                <span>{Notir}</span>
              </Notificationrouge>
            )}
            {Notiv && (
              <Notificationvert>
                <span>{Notiv}</span>
              </Notificationvert>
            )}
            <StyledButton type="submit">
              Inscrivez-vous{console.log(state)}
            </StyledButton>
            <StyledAddiction>
              <Styledp>Vous avez déjà un compte?</Styledp>
              <Styleda to="/LogIn">Connectez-vous</Styleda>
            </StyledAddiction>
          </StyledForm>
        </StyledFormContainer>
      </StyledFormWrapper>
    </div>
  );
};

export default Gstock;
