import React, { useEffect, useState } from "react";
import "./Catalogue_style.css";
import axios from "axios";
import { StyledFormContainer, StyledFormWrapper } from "./CatalogueElements.js";
export const Catalogue = () => {
  const [Products, setProducts] = useState([]);
  useEffect(() => {
    axios
      .get("https://productsapi1.herokuapp.com/api/produits")
      .then((Response) => {
        setProducts(Response.data);
      })
      .catch((error) => {});
  }, [Products]);
  return (
    <div>
      <StyledFormWrapper>
        <StyledFormContainer>
          {Products.map((product) => {
            return (
              <div class="el-wrapper">
                <div class="box-up">
                  <img
                    class="img"
                    src="http://code.slicecrowd.com/labs/4/images/t-shirt.png"
                    alt=""
                  />
                  <div class="img-info">
                    <div class="info-inner">
                      <span class="p-name">{product.nom}</span>
                      <span class="p-company">{product.type}</span>
                    </div>
                    <div class="a-size">
                      <span class="size">
                        Desciption :{product.description}
                      </span>
                    </div>
                  </div>
                </div>

                <div class="box-down">
                  <div class="h-bg">
                    <div class="h-bg-inner"></div>
                  </div>

                  <a class="cart" href="#">
                    <span class="price">{product.prix}$</span>
                    <span class="add-to-cart">
                      <span class="txt">Ajouter au panier</span>
                    </span>
                  </a>
                </div>
              </div>
            );
          })}
        </StyledFormContainer>
      </StyledFormWrapper>
    </div>
  );
};

export default Catalogue;
