import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
} from "./PanierElements.js";

const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState([1]);
  const [Idpanier, setIdPanier] = useState([1]);
  useEffect(() => {
    const email = localStorage.getItem("email");
    const getPanier = async () => {
      const reponse = await axios
        .get(
          `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
        )
        .then((result) => {
          return result.data.items;
        })
        .catch((err) => {
          console.log(err);
        });
      console.log(reponse);
      console.log(typeof reponse);
      setCurrentPanier(reponse);
      return reponse;
    };
    getPanier();
  }, []);
  useEffect(() => {
    let Product_ID = [];
    CurrentPanier.map((produit) => {
      Product_ID.push(produit.product_id);
    });
    console.log(Product_ID);
  }, [CurrentPanier]);
  return (
    <PanierWrapper>
      <PanierContainer>
        {Array.from(CurrentPanier).map((produit, key) => {
          return (
            <ProduitItem>
              <p>{produit.qty}</p>
            </ProduitItem>
          );
        })}
      </PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
