import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
  ImgPanier,
  ProduitContainer,
  LeftSide,
  RightSide,
  Nomp,
  LeftSideInfo,
  ProductsContainer,
  RemoveProduit,
} from "./PanierElements.js";

const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState([]);
  const [Products, setProducts] = useState([]);

  useEffect(() => {
    const email = localStorage.getItem("email");
    const getPanier = async () => {
      const reponse = await axios
        .get(
          `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
        )
        .then((result) => {
          return result.data.items;
        })
        .catch((err) => {
          console.log(err);
        });
      setCurrentPanier(reponse);
      console.log(reponse);
    };
    getPanier();
  }, []);
  useEffect(() => {
    setProducts([]);
    CurrentPanier.map((id) => {
      console.log(id.product_id);
      const idtoProduct = async () => {
        const reponse = await axios
          .get(
            `https://whispering-bastion-00988.herokuapp.com/api/produits/${id.product_id}`
          )
          .then((result) => {
            console.log(result.data);
            return result.data;
          })
          .catch((err) => {
            console.log(err.message);
          });
        console.log(reponse);
        if (!Products.includes(reponse)) {
          reponse.qtycmd = id.qty;
          setProducts((prevProducts) => [...prevProducts, reponse]);
        }
      };
      idtoProduct();
    });
  }, [CurrentPanier]);
  const SupprimerProduit = (product_id) => {
    const email = localStorage.getItem("email");
    console.log(product_id);
    console.log(email);
    console.log(typeof product_id);
    axios.delete(
      `https://managecartandorders.herokuapp.com/api/cart/deleteProduit`,
      { email: email, product_id: product_id }
    );
  };
  return (
    <PanierWrapper>
      <PanierContainer>
        <LeftSide>
          <ProductsContainer>
            {Products.map((p, key) => {
              return (
                <ProduitItem>
                  <ProduitContainer>
                    <ImgPanier src={p.imageurl} />
                    <LeftSideInfo>
                      <Nomp>{p.nom}</Nomp>
                      <p>Quantité : {p.qtycmd}</p>
                      <RemoveProduit
                        onClick={() => {
                          SupprimerProduit(p.id);
                        }}
                      >
                        Supprimer le produit
                      </RemoveProduit>
                    </LeftSideInfo>
                  </ProduitContainer>
                </ProduitItem>
              );
            })}
          </ProductsContainer>
        </LeftSide>
        <RightSide></RightSide>
      </PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
