import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
} from "./PanierElements.js";

const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState([]);
  useEffect(() => {
    const email = localStorage.getItem("email");
    const getPanier = async (email) => {
      const reponse = await axios
        .get(
          `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
        )
        .then((result) => {
          return result.data.items;
        })
        .catch((err) => {
          console.log(err);
        });
      console.log(reponse);
      return reponse;
    };
    setCurrentPanier(getPanier(email));
    console.log(CurrentPanier);
  }, []);
  return (
    <PanierWrapper>
      <PanierContainer>
        <ProduitItem>
          {Object.keys(CurrentPanier).map((produit, key) => {
            <h1>produit.qty</h1>;
          })}
        </ProduitItem>
      </PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
