import axios from "axios";
import React, { useEffect, useRef, useState } from "react";
import {
  GStockContainer,
  Wrapper,
  TableTh,
  GStockTable,
  TableTd,
  TableLink,
  TableButton,
  BtnAjouterP,
  Form,
  ButtonR,
  Input,
} from "./GOrderElements.js";
import { VscCheck, VscClose } from "react-icons/vsc";
import { MdDelete, MdModeEdit } from "react-icons/md";
import { BiSearch } from "react-icons/bi";
import { RiAddCircleLine } from "react-icons/ri";
const GOrder = () => {
  const [Commandes, setCommandes] = useState([]);
  let Compteur = 0;
  const [input, setInput] = useState("");
  const [barOpened, setBarOpened] = useState(false);
  const formRef = useRef();
  const inputFocus = useRef();

  const onFormSubmit = (e) => {
    // When form submited, clear input, close the searchbar and do something with input
    e.preventDefault();
    setInput("");
    setBarOpened(true);
    // After form submit, do what you want with the input value
    console.log(`Form was submited with input: ${input}`);
  };
  useEffect(() => {
    axios
      .get("https://managecartandorders.herokuapp.com/api/order/orders")
      .then((Response) => {
        setCommandes(Response.data.reverse());
        console.log(Commandes);
        console.log(typeof Commandes);
      })
      .catch((error) => {});
  }, [Commandes]);
  const HandleEtat = (nombre) => {
    if (nombre > 0) {
      return <VscCheck color="green" />;
    } else return <VscClose color="red" />;
  };
  const DeleteHandler = (id) => {
    axios
      .delete(
        `https://whispering-bastion-00988.herokuapp.com/api/produits/${id}`
      )
      .then((response) => {
        console.log(response.data);
      })
      .catch();
  };
  const DisplayCompteur = () => {
    Compteur++;
    return Compteur;
  };
  return (
    <div>
      <Wrapper>
        <GStockContainer>
          <BtnAjouterP to="/GestionProduit/AjouterProduit">
            <RiAddCircleLine size="48" />
          </BtnAjouterP>
          <h3>Ajouter un produit</h3>
          <Form
            barOpened={barOpened}
            onClick={() => {
              // When form clicked, set state of baropened to true and focus the input
              setBarOpened(true);
              inputFocus.current.focus();
            }}
            // on focus open search bar
            onFocus={() => {
              setBarOpened(true);
              inputFocus.current.focus();
            }}
            // on blur close search bar
            onBlur={() => {
              setBarOpened(false);
            }}
            // On submit, call the onFormSubmit function
            onSubmit={onFormSubmit}
            ref={formRef}
          >
            <ButtonR type="submit" barOpened={barOpened}>
              <BiSearch />
            </ButtonR>
            <Input
              onChange={(e) => {
                setInput(e.target.value);
              }}
              ref={inputFocus}
              value={input}
              barOpened={barOpened}
              placeholder="Rechercher un produit par son nom..."
            />
          </Form>
          <GStockTable>
            <tr>
              <TableTh>Num Commande</TableTh>
              <TableTh>Nom</TableTh>
              <TableTh>Prix Totale</TableTh>
              <TableTh>Num Tel</TableTh>
              <TableTh>Quantité</TableTh>
              <TableTh>État</TableTh>
              <TableTh>Modifier</TableTh>
              <TableTh>Supprimer</TableTh>
            </tr>
            {Commandes.filter((commande) => {
              if (input === "") {
                return commande;
              } else if (
                commande.nom.toLowerCase().includes(input.toLowerCase())
              ) {
                return commande;
              }
            }).map((commande, key) => {
              return (
                <tr id={commande.id} key={key}>
                  <TableTd>{DisplayCompteur()}</TableTd>
                  <TableTd>{commande.nom}</TableTd>

                  <TableTd>{commande.grandTotal}</TableTd>

                  <TableTd>{commande.quantite}</TableTd>
                  <TableTd>{HandleEtat(commande.quantite)}</TableTd>
                  <TableTd>
                    <TableLink
                      to={{
                        pathname: `/AdminDashboard/GestionProduit/ModifierProduit/${commande.id}`,
                        id: commande.id,
                      }}
                    >
                      <MdModeEdit color="green" size="24" />
                    </TableLink>
                  </TableTd>
                  <TableTd>
                    <TableButton
                      onClick={() => {
                        if (
                          window.confirm(
                            "Voulez-vous vraiment supprimer ce produit?"
                          )
                        ) {
                          DeleteHandler(commande.id);
                          setTimeout(() => {
                            alert("Produit supprimé avec succès !");
                          }, 1500);
                        }
                      }}
                    >
                      <MdDelete color="red" size="24" />
                    </TableButton>
                  </TableTd>
                </tr>
              );
            })}
          </GStockTable>
        </GStockContainer>
      </Wrapper>
    </div>
  );
};

export default GOrder;
