import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
} from "./PanierElements.js";

const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState([]);
  useEffect(() => {
    const email = localStorage.getItem("email");
    const getPanier = async (email) => {
      const reponse = await axios
        .get(
          `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
        )
        .then((result) => {
          return result.data.items;
        })
        .catch((err) => {
          console.log(err);
        });
      console.log(reponse);
      console.log(typeof reponse);
      reponse = Array.from(reponse);
      console.log(typeof reponse);
      return reponse;
    };
    setCurrentPanier(getPanier());
    console.log(CurrentPanier);
  }, []);
  return (
    <PanierWrapper>
      <PanierContainer>
        {CurrentPanier.map((produit, key) => {
          return <ProduitItem key={key}>{produit.qty}</ProduitItem>;
        })}
      </PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
