import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
} from "./PanierElements.js";

const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState();
  useEffect(() => {
    const email = localStorage.getItem("email");
    const getPanier = async () => {
      const reponse = await axios
        .get(
          `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
        )
        .then((reponse) => {
          console.log(CurrentPanier);
        })
        .catch((err) => {
          console.log(err);
        });
      setCurrentPanier(getPanier.data.items);
    };
  });
  return (
    <PanierWrapper>
      <PanierContainer>
        {CurrentPanier.map((produit, key) => {
          <ProduitItem></ProduitItem>;
        })}
      </PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
