import axios from "axios";
import React, { useEffect, useState } from "react";
import {
  ProduitWrapper,
  ProduitContainer,
  ImgProduit,
} from "./FicheProduitElements.js";
function FicheProduit(props) {
  const [CurrentProduct, setCurrentProduct] = useState({});
  useEffect(() => {
    axios
      .get(
        `https://whispering-bastion-00988.herokuapp.com/api/produits/${props.location.pathname.substring(
          11
        )}`
      )
      .then((reponse) => {
        setCurrentProduct(reponse.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    <ProduitWrapper>
      <ProduitContainer>
        <ImgProduit src={CurrentProduct.img} />
        <p>{CurrentProduct.nom}</p>
      </ProduitContainer>
    </ProduitWrapper>
  );
}

export default FicheProduit;
