import axios from "axios";
import React, { useEffect, useState } from "react";
import {
  ProduitWrapper,
  ProduitContainer,
  ListeProduit,
  InfoProduitContainer,
  GStockContainer,
  Wrapper,
  TableTh,
  GStockTable,
  TableTd,
  TableLink,
  TableButton,
  StyledTitle,
  Form,
  ButtonR,
  Input,
  StyledAcceder,
  PasEncore,
} from "./CommandeElements.js";
import "./Commande_style.css";
function Commande(props) {
  const [CurrentProduct, setCurrentProduct] = useState({});
  const [jLivraison, setJLivraison] = useState(1);
  const [Lproduits, setLProduits] = useState({});
  useEffect(() => {
    console.log(props.location.pathname.substring(26));
    axios
      .get(
        `https://managecartandorders.herokuapp.com/api/order/${props.location.pathname.substring(
          26
        )}`
      )
      .then((reponse) => {
        setCurrentProduct(reponse.data);
        console.log(reponse.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  const incrementeJLivraison = () => {
    setJLivraison(jLivraison + 1);
  };
  const decrementeJLivraison = () => {
    if (jLivraison !== 1) setJLivraison(jLivraison - 1);
  };
  const QtyHandler = (e, qty) => {
    const value = e.currentTarget.value;
    setJLivraison(value);
    if (value > qty) {
      alert(
        "La quantité voulue est supérieure à la quantité en stock veuillez changer de quantité!"
      );
    }
  };
  const acceptOrder = () => {
    axios
      .put(
        `https://managecartandorders.herokuapp.com/api/order/${props.location.pathname.substring(
          26
        )}`,
        {
          accepted: true,
          deliveryTime: jLivraison,
        }
      )
      .then((response) => {
        setTimeout(alert("Commande acceptée avec succès"), 3000);
      });
  };
  return (
    <ProduitWrapper>
      <ProduitContainer>
        <ListeProduit>
          <GStockTable>
            <tr>
              <TableTh>Num Commande</TableTh>
              <TableTh>Nom du client</TableTh>
              <TableTh>Prix Totale</TableTh>
              <TableTh>Num Tel</TableTh>
              <TableTh>Methode de paiements</TableTh>
              <TableTh>Commande accepté</TableTh>
              <TableTh>Détails Commande</TableTh>
              <TableTh>Supprimer Commande</TableTh>
            </tr>
            {Commandes.filter((commande) => {
              if (input === "") {
                return commande;
              } else if (
                commande.nom.toLowerCase().includes(input.toLowerCase())
              ) {
                return commande;
              }
            }).map((commande, key) => {
              return (
                <tr id={commande.id} key={key}>
                  <TableTd>{DisplayCompteur()}</TableTd>
                  <TableTd>{commande.user.name}</TableTd>

                  <TableTd>{commande.grandTotal}$</TableTd>

                  <TableTd>{commande.phoneNumber}</TableTd>
                  <TableTd>{commande.paymentMethod}</TableTd>
                  <TableTd>{HandleEtat(commande.accepted)}</TableTd>
                  <TableTd>
                    <TableLink
                      to={{
                        pathname: `/GestionCommande/Commande/${commande._id}`,
                        id: commande._id,
                      }}
                    >
                      <StyledAcceder>Accéder à la commande</StyledAcceder>
                    </TableLink>
                  </TableTd>
                  <TableTd>
                    <TableButton
                      onClick={() => {
                        if (
                          window.confirm(
                            "Voulez-vous vraiment supprimer cette commande?"
                          )
                        ) {
                          DeleteHandler(commande._id);
                          setTimeout(() => {
                            alert("Commande supprimé avec succès !");
                          }, 1500);
                        }
                      }}
                    >
                      <MdDelete color="red" size="24" />
                    </TableButton>
                  </TableTd>
                </tr>
              );
            })}
          </GStockTable>
        </ListeProduit>
        <InfoProduitContainer>
          <h1>{CurrentProduct.nom}</h1>
          <br />
          <h3>{CurrentProduct.prix}$</h3>
          <br />
          <p>{CurrentProduct.description}</p>
          <br />
          <h4>Jours de livraison : {CurrentProduct.quantite}</h4>
          <br />
          <div class="quantity buttons_added">
            <input
              type="button"
              value="-"
              class="minus"
              onClick={decrementeJLivraison}
            />
            <input
              type="number"
              step="1"
              min="1"
              max={CurrentProduct.quantite}
              name="quantity"
              value={jLivraison}
              title="jLivraison"
              class="input-text qty text"
              size="4"
              pattern=""
              inputmode=""
              onChange={(e) => {
                QtyHandler(e, CurrentProduct.quantite);
              }}
            />
            <input
              type="button"
              value="+"
              class="plus"
              onClick={(e) => {
                incrementeJLivraison(e);
              }}
            />
          </div>
          <br />
          <center>
            <button className="btn_addcart" onClick={acceptOrder}>
              Accepter La commande
            </button>
          </center>
        </InfoProduitContainer>
      </ProduitContainer>
    </ProduitWrapper>
  );
}

export default Commande;
