import React, { useState } from "react";
import axios from "axios";
import {
  StyledFormWrapper,
  StyledForm,
  StyledInput,
  StyledButton,
  StyledError,
  Notificationvert,
  Notificationrouge,
  Styledh1,
  StyledFormBlue,
  Styledp,
  Styleda,
  StyledAddiction,
  StyledFormContainer,
} from "../../GlobalStyles.js";

const initalState = {
  nom: "",
  prenom: "",
  email: "",
  numtel: "",
  password: "",
  roles: "user",
};
export const SignIn = () => {
  const [state, setState] = useState(initalState);
  const [error, setError] = useState("");
  const [Notiv, setNotiv] = useState("");
  const [Notir, setNotir] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("submitted!");
    console.log(state);

    for (let key in state) {
      if (state[key] === "") {
        setError(`You must provide the ${key}`);
        return;
      }
    }
    setError("");
    axios
      .post(
        "https://fathomless-coast-11439.herokuapp.com/api/auth/signup",
        state
      )
      .then((response) => {
        setNotir("");
        setNotiv(response.data.message);
      })
      .catch((erreur) => {
        setNotiv("");
        setNotir(erreur.response.data.message);
      });
  };

  const handleInput = (e) => {
    const inputName = e.currentTarget.name;
    if (inputName == "numtel") {
      if (inputName.length > 10) {
        setError("Entrez un num de tel");
      }
    }
    const value = e.currentTarget.value;

    setState((prev) => ({ ...prev, [inputName]: value }));
  };
  return (
    <div>
      <StyledFormWrapper>
        <StyledFormContainer>
          <StyledForm onSubmit={handleSubmit}>
            <Styledh1>Inscrivez-Vous !</Styledh1>
            <br />
            <label htmlFor="nom"></label>
            <StyledInput
              type="text"
              name="nom"
              value={state.nom}
              onChange={handleInput}
              placeholder="Nom..."
            />
            <label htmlFor="prenom"></label>
            <StyledInput
              type="text"
              name="prenom"
              value={state.prenom}
              onChange={handleInput}
              placeholder="Prénom..."
            />
            <label htmlFor="email"></label>
            <StyledInput
              type="email"
              name="email"
              value={state.email}
              onChange={handleInput}
              placeholder="Email..."
            />
            <label htmlFor="tel"></label>
            <StyledInput
              type="tel"
              name="numtel"
              value={state.numtel}
              onChange={handleInput}
              placeholder="Numéro de téléphone..."
            />
            <label htmlFor="password"></label>
            <StyledInput
              type="password"
              name="password"
              value={state.password}
              onChange={handleInput}
              placeholder="Mot de passe..."
            />
            {error && (
              <StyledError>
                <p>{error}</p>
              </StyledError>
            )}
            <StyledButton type="submit">
              Inscrivez-vous{console.log(state)}
            </StyledButton>
            <StyledAddiction>
              <Styledp>Vous n'avez pas de compte?</Styledp>
              <Styleda>Inscrivez-vous</Styleda>
            </StyledAddiction>
          </StyledForm>
          <StyledFormBlue>
            <img src="" />
          </StyledFormBlue>
        </StyledFormContainer>
      </StyledFormWrapper>
      {Notir && (
        <Notificationrouge>
          <span>{Notir}</span>
        </Notificationrouge>
      )}
      {Notiv && (
        <Notificationvert>
          <span>{Notiv}</span>
        </Notificationvert>
      )}
    </div>
  );
};

export default SignIn;
