import axios from "axios";
import React, { useEffect, useState } from "react";
import {
  GStockContainer,
  Wrapper,
  TableTh,
  GStockTable,
  ProductImg,
} from "./GstockElements.js";
import { VscCheck } from "react-icons/vsc";
const Gstock = () => {
  const [Products, setProducts] = useState([]);
  const [Compteur, setCompteur] = useState(0);
  useEffect(() => {
    axios
      .get("https://productsapi1.herokuapp.com/api/produits")
      .then((Response) => {
        setProducts(Response.data);
        console.log(Products);
      })
      .catch((error) => {});
  }, [Products]);
  const HandleEtat = (nombre) => {
    if (nombre > 0) {
      return <img src={VscCheck} />;
    } else return <img src={VscClose} />;
  };
  return (
    <div>
      <Wrapper>
        <GStockContainer>
          <GStockTable>
            <tr>
              <TableTh>Num Produits</TableTh>
              <TableTh>Nom</TableTh>
              <TableTh>Prix</TableTh>
              <TableTh>Image</TableTh>
              <TableTh>Quantité</TableTh>
              <TableTh>État</TableTh>
            </tr>
            {Products.map((product) => {
              return (
                <tr id={product.id}>
                  <td>{Compteur}</td>
                  <td>{product.nom}</td>
                  <td>{product.prix}</td>
                  <td>
                    <ProductImg src={product.imageurl}></ProductImg>
                  </td>
                  <td>{product.quantite}</td>
                  <td>
                    {() => {
                      HandleEtat(product.quantite);
                    }}
                  </td>
                </tr>
              );
            })}
          </GStockTable>
        </GStockContainer>
      </Wrapper>
    </div>
  );
};

export default Gstock;
