import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  PanierWrapper,
  PanierContainer,
  ProduitItem,
} from "./PanierElements.js";

const Panier = () => {
  const [CurrentPanier, setCurrentPanier] = useState();
  useEffect(() => {
    const email = localStorage.getItem("email");
    const getPanier = async () => {
      const reponse = await axios.get(
        `https://managecartandorders.herokuapp.com/api/cart/cart?email=${email}`
      );

      getPanier();
      setCurrentPanier(reponse.data.items);
      console.log(CurrentPanier);
    };
  });
  return (
    <PanierWrapper>
      <PanierContainer></PanierContainer>
    </PanierWrapper>
  );
};

export default Panier;
