import React, { useState } from "react";
import { Redirect, Route } from "react-router-dom";
import {
  DashboardContainer,
  Dashboard,
  DashBoardContent,
  DashboardList,
  DashboardListItem,
  Listp,
} from "./AdminDashboardElements.js";
import { BiPackage, BiUserCircle } from "react-icons/bi";
import { FaUserFriends } from "react-icons/fa";
import MonProfile from "../MonProfile/MonProfile.js";
import GestionStock from "../MonProfile/MonProfile.js";
import GestionUtilisateur from "../MonProfile/MonProfile.js";

export const AdminDashboard = ({ authorized }) => {
  if (!authorized) {
    return <Redirect to="/LogIn" />;
  }

  const nom = localStorage.getItem("nom");
  const prenom = localStorage.getItem("prenom");
  return (
    <DashboardContainer>
      <Dashboard>
        <DashboardList>
          <DashboardListItem to="/AdminDashboard/MonProfile">
            <BiUserCircle size="32" />
            <Listp>Mon Profil</Listp>
          </DashboardListItem>
          <DashboardListItem to="/AdminDashboard/GestionProduit">
            <BiPackage size="32" />
            <Listp>Gestion des produits</Listp>
          </DashboardListItem>
          <DashboardListItem to="/AdminDashboard/GestionUtilisateur">
            <FaUserFriends size="32" />
            <Listp>Gestion des utilisateurs</Listp>
          </DashboardListItem>
        </DashboardList>

        <DashBoardContent>
          <switch>
            <Route
              path="/AdminDashboard/MonProfile"
              exact
              component={MonProfile}
            ></Route>
            <route
              path="/AdminDashboard/GestionProduit"
              exact
              component={GestionUtilisateur}
            ></route>
            <Route
              path="/AdminDashboard/GestionUtilisateur"
              exact
              component={GestionStock}
            ></Route>
          </switch>
        </DashBoardContent>
      </Dashboard>
    </DashboardContainer>
  );
};
export default AdminDashboard;
